package com.example.moviecatalogue2.Model;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface RequestRelease {    @GET("3/discover/movie")    Call<ReleaseResults> getUpcomingFilm(            @Query("api_key") String api_key,            @Query("primary_release_date.gte") String date_gte,            @Query("primary_release_date.lte") String date_lte    );}